import requestsimport reclass Doutu():    num =0    def qingqiu(self,i):        url = 'http://www.doutula.com/article/list/?page={}'.format(i)        self.head = {'User-Agent': 'Mozilla/5.0 (Windows NT 6.1; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/69.0.3497.100 Safari/537.36'}        aaa = requests.get(url=url,headers=self.head)        html = aaa.content.decode('UTF-8')        return html    def guolv(self,html):        patt = re.compile('http://www.doutula.com/article/detail/[0-9]{7}')        items = patt.findall(html)        return items    def aaa(self,items):        for i in items:            bbb = requests.get(url=i,headers=self.head)            html1 = bbb.content.decode('UTF-8')            patt1 = re.compile('"this.src=(.*?)">')            items1 = patt1.findall(html1)            return items1    def save(self,items1):        for i in items1:            bbb = requests.get(i, headers=self.head)            html2 = bbb.content.decode('UTF-8')            patt2 = re.compile('this.src=(.*?)"></a>')            items2 = patt2.findall(html2)            print(len(i))            return items2    def savea(self,items2):        for i in items2:            tupian = requests.get(i)            res1 = tupian.content            with open(r'D:\aaa\{}.jpg'.format(self.num),'ab') as f:                f.write(res1)                self.num+=1for i in range(1,11):    doutu = Doutu()    aa=doutu.qingqiu(i)    bb=doutu.guolv(aa)    cc=doutu.aaa(bb)    dd=doutu.save(cc)    doutu.savea(dd)